import { Form, Input, InputNumber, Col, Row, Tooltip, Alert } from "antd";
import React, {useState} from 'react';
const InputGroup = Input.Group;
const Glory = () => {
  const [building, setBuilding] = useState({
    fire: 0,
    spd: 0,
    atk: 0,
    hp: 0,
    def: 0,
    criDmg: 0,
  });
  const updateField = e => {
    console.log("e.target", e, e.target);
    setBuilding(
      {
        ...building,
        [e.target.name]: e.target.value
      },
      console.log("실행되네")
    );
  };
  return (
    <div className="glory">
      <p>(Glory building) 명건작</p>
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal">
        <Form.Item label="Fire Building">
          <Form.Item name="addCriDmg1" onChange={updateField}>
            <Tooltip placement="topLeft" title="불속성 공격력 건물">
              <InputNumber name="addCriDmg1" placeholder="불속성 공격력 건물" />
            </Tooltip>
          </Form.Item>
          <Alert message="Success" type="success" showIcon />
        </Form.Item>
        <Form.Item label="Speed">
          <Form.Item name="spd" onChange={updateField}>
            <Tooltip placement="topLeft" title="공속건물">
              <InputNumber name="addCriDmg1" placeholder="공속" />
            </Tooltip>
          </Form.Item>
          <Alert message="Success" type="success" showIcon />
        </Form.Item>
        <Form.Item label="Atk">
          <Form.Item name="atk" onChange={updateField}>
            <Tooltip placement="topLeft" title="공격력 건물">
              <InputNumber name="atk" placeholder="공격력" />
            </Tooltip>
          </Form.Item>
          <Alert message="Success" type="success" showIcon />
        </Form.Item>
        <Form.Item label="HP">
          <Form.Item name="atk" onChange={updateField}>
            <Tooltip placement="topLeft" title="체력 건물">
              <InputNumber name="atk" placeholder="체력" />
            </Tooltip>
          </Form.Item>
          <Alert message="Success" type="success" showIcon />
        </Form.Item>
      </Form>
    </div>
  );
};

export default Glory;
