import React, { useState } from "react";
import { Form, InputNumber } from "antd";
const BasicForm = ({ hp, atk, def, spd, criRate, criDmg, onChange }) => {
  const [componentSize, setComponentSize] = useState("small");
  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  const changeHandler = name => value => {
    console.log(name, value);
    this.props.onChange();
  };
  return (
    <div className="basic-form">
      <style jsx>{`
        .basic-form:global(.ant-input-number-input) {
          margin-right: 10px;
        }
      `}</style>
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        initialValues={{ size: componentSize }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
      >
        <Form.Item label="HP">
          <InputNumber disabled defaultValue={hp} />
          <InputNumber name="hp" onChange={changeHandler} />
        </Form.Item>
        <Form.Item label="ATK">
          <InputNumber disabled defaultValue={atk} />
          <InputNumber name="atk" onChange={changeHandler} />
        </Form.Item>
        <Form.Item label="DEF">
          <InputNumber disabled defaultValue={def} />
          <InputNumber name="edf" onChange={changeHandler} />
        </Form.Item>
        <Form.Item label="SPD">
          <InputNumber disabled defaultValue={spd} />
          <InputNumber name="spd" onChange={changeHandler} />
        </Form.Item>
        <Form.Item label="CRI RATE">
          <InputNumber
            disabled
            defaultValue={criRate}
            min={0}
            max={100}
            formatter={value => `${value}%`}
          />
          <InputNumber name="criRate" onChange={changeHandler} />
        </Form.Item>
        <Form.Item label="CRI DMG">
          <InputNumber
            disabled
            defaultValue={criDmg}
            min={0}
            max={1000}
            formatter={value => `${value}%`}
          />
          <InputNumber name="criDmg" onChange={changeHandler} />
        </Form.Item>
      </Form>
    </div>
  );
};
export default BasicForm;
