import { Form, Input, InputNumber, Col, Row, Tooltip, Alert } from "antd";
import React, {useState} from 'react';
const InputGroup = Input.Group;
const Guild = () => {
	const [building, setBuilding] = useState({
		fire: 0,
		spd: 0,
		atk: 0,
		hp: 0,
		def: 0,
		criDmg: 0,
	});
	const updateField = e => {
		console.log("e.target", e, e.target);
		setBuilding(
			{
				...building,
				[e.target.name]: e.target.value
			},
			console.log("실행되네")
		);
	};
	return (
		<div className="glory">
			<p>(Glory building) 명건작</p>
			<Form
				labelCol={{ span: 4 }}
				wrapperCol={{ span: 14 }}
				layout="horizontal">
				<Form.Item label="CRI_DMG">
					<InputGroup>
						<Row gutter={16}>
							<Col span={6}>
								<Form.Item name="addCriDmg1" onChange={updateField}>
									<Tooltip placement="topLeft" title="뒷치피">
										<InputNumber name="addCriDmg1" placeholder="뒷치피" />
									</Tooltip>
								</Form.Item>
							</Col>
							<Col span={6}>
								<Form.Item name="addCriDmg2" onChange={updateField}>
									<Form.Item name="addCriDmg2" onChange={updateField}>
										<Tooltip placement="topLeft" title="추가 치피">
											<InputNumber name="addCriDmg2" placeholder="추가 치피" />
										</Tooltip>
									</Form.Item>
								</Form.Item>
							</Col>
						</Row>
					</InputGroup>
					<Alert message="Success" type="success" showIcon />
				</Form.Item>
			</Form>
		</div>
	);
};

export default Guild;
