import { useState } from "react";
import { LIGHTNING_EMPEROR } from "../ spec/LightningEmperor-spec";
import BasicForm from "../base/Form";
import { Form, InputNumber } from "antd";
import React from "react";
const LightningEmperor = () => {
  const { BALEYAR } = LIGHTNING_EMPEROR;
  const { HP, ATK, DEF, SPD, CRI_RATE, CRI_DMG } = BALEYAR;
  const [form, setForm] = useState({
    hp: 0,
    addHp: 0,
    expectHp: 0,
    atk: 0,
    addAtk: 0,
    expectAtk: 0,
    def: 0,
    addDef: 0,
    expectDef: 0,
    spd: 0,
    addSpd: 0,
    expectSpd: 0,
    criRate: 0,
    addCriRate: 0,
    expectCriRate: 0,
    criDmg: 0,
    addCriDmg: 0,
    expectCriDmg: 0
  });
  const updateField = e => {
    console.log("e.target", e, e.target);
    setForm(
      {
        ...form,
        [e.target.id]: e.target.value
      },
      console.log("실행되네")
    );
  };
  return (
    <div>
      <p>(baleygr)불뇌제 스펙스펙</p>
      <BasicForm
        hp={HP}
        atk={ATK}
        def={DEF}
        spd={SPD}
        criRate={CRI_RATE}
        criDmg={CRI_DMG}
        expectHp={expectHp}
        onChange={updateField}
      />
    </div>
  );
};

export default LightningEmperor;
