import React, { useState } from "react";
import {
  Form,
  Input,
  Button,
  Radio,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  TreeSelect,
  Switch
} from "antd";
const BasicForm = ({ hp, atk, def, spd, criRate, criDmg }) => {
  const [componentSize, setComponentSize] = useState("small");
  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  return (
    <div>
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        initialValues={{ size: componentSize }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
      >
        <Form.Item label="HP">
          <InputNumber />
        </Form.Item>
        <Form.Item label="ATK">
          <InputNumber disabled />
          <InputNumber disabled />
        </Form.Item>
        <Form.Item label="DEF">
          <InputNumber />
        </Form.Item>
        <Form.Item label="SPD">
          <InputNumber />
        </Form.Item>
        <Form.Item label="CRI RATE">
          <InputNumber />
        </Form.Item>
        <Form.Item label="CRI DMG">
          <InputNumber />
        </Form.Item>
      </Form>
    </div>
  );
};
export default BasicForm;
