import React, { useState } from "react";
import { Form, InputNumber } from "antd";
const BasicForm = ({ hp, atk, def, spd, criRate, criDmg }) => {
  const [componentSize, setComponentSize] = useState("small");
  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  return (
    <div>
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        initialValues={{ size: componentSize }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
      >
        <Form.Item label="HP">
          <InputNumber disabled defaultValue={hp} />
        </Form.Item>
        <Form.Item label="ATK">
          <InputNumber disabled defaultValue={atk} />
          <InputNumber />
        </Form.Item>
        <Form.Item label="DEF" disabled defaultValue={def}>
          <InputNumber />
        </Form.Item>
        <Form.Item label="SPD" disabled defaultValue={spd}>
          <InputNumber />
        </Form.Item>
        <Form.Item label="CRI RATE" disabled defaultValue={criRate}>
          <InputNumber />
        </Form.Item>
        <Form.Item label="CRI DMG" disabled defaultValue={criDmg}>
          <InputNumber />
        </Form.Item>
      </Form>
    </div>
  );
};
export default BasicForm;
