import { useState } from "react";
import { LIGHTNING_EMPEROR } from "../ spec/LightningEmperor-spec";
import BasicForm from "../base/Form";
import { Form, InputNumber } from "antd";
import React from "react";
const LightningEmperor = () => {
  const { BALEYAR } = LIGHTNING_EMPEROR;
  const { HP, ATK, DEF, SPD, CRI_RATE, CRI_DMG } = BALEYAR;
  const [form, setForm] = useState({
    now: { hp: 0, atk: 0, def: 0, spd: 0, criRate: 0, criDmg: 0 }
  });
  const updateField = e => {
    console.log(e.target);
    setForm({
      ...form,
      now: {
        ...form.now,
        [e.target.name]: e.target.value
      }
    });
  };
  return (
    <div>
      <p>(baleygr)불뇌제 스펙스펙</p>
      <BasicForm
        hp={HP}
        atk={ATK}
        def={DEF}
        spd={SPD}
        criRate={CRI_RATE}
        criDmg={CRI_DMG}
        onChange={updateField}
      />
    </div>
  );
};

export default LightningEmperor;
