import React, { useState } from "react";
import { Form, Input, InputNumber, Col, Row } from "antd";
const InputGroup = Input.Group;
const BasicForm = ({ hp, atk, def, spd, criRate, criDmg, onChange }) => {
  const [componentSize, setComponentSize] = useState("small");
  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };

  const changeHandler = name => value => {
    console.log(name, value);
    this.props.onChange();
  };
  return (
    <div className="basic-form">
      <style jsx>{`
        .basic-form:global(.ant-input-number-input) {
          margin-right: 10px;
        }
      `}</style>
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        initialValues={{ size: componentSize }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
      >
        <Form.Item label="HP">
          <InputGroup size="small">
            <Row gutter={8}>
              <Col span={5}>
                <InputNumber disabled defaultValue={hp} />
              </Col>
              <Col span={5}>
                <Form.Item name="hp" onChange={onChange} >
                  <Input default/>
                </Form.Item>
              </Col>
              <Col span={5}>
                <Form.Item name="addHp" onChange={onChange}>
                  <Input />
                </Form.Item>
              </Col>
            </Row>
          </InputGroup>
        </Form.Item>

        <Form.Item label="ATK">
          <InputNumber disabled defaultValue={atk} />
          <Form.Item name="atk" onChange={onChange}>
            <Input />
          </Form.Item>
        </Form.Item>
        <Form.Item label="DEF">
          <InputNumber disabled defaultValue={def} />
          <Form.Item name="def" onChange={onChange}>
            <Input />
          </Form.Item>
        </Form.Item>
        <Form.Item label="SPD">
          <InputNumber disabled defaultValue={spd} />
          <Form.Item name="spd" onChange={onChange}>
            <Input />
          </Form.Item>
        </Form.Item>
        <Form.Item label="CRI RATE">
          <InputNumber
            disabled
            defaultValue={criRate}
            min={0}
            max={100}
            formatter={value => `${value}%`}
          />
          <Form.Item name="criRate" onChange={onChange}>
            <Input />
          </Form.Item>
        </Form.Item>
        <Form.Item label="CRI DMG">
          <InputNumber
            disabled
            defaultValue={criDmg}
            min={0}
            max={1000}
            formatter={value => `${value}%`}
          />
          <Form.Item name="criDmg" onChange={onChange}>
            <Input />
          </Form.Item>
        </Form.Item>
      </Form>
    </div>
  );
};
export default BasicForm;
